import React, { useRef, useEffect } from "react";
import io from "socket.io-client";

const Room = (props) => {
    const userVideo = useRef();
    const partnerVideo = useRef();
    const peerRef = useRef();
    const socketRef = useRef();
    const otherUser = useRef();
    const userStream = useRef();

    useEffect(() => {
        navigator.mediaDevices.getUserMedia({ audio: true, video: true }).then(stream => {
            userVideo.current.srcObject = stream;
            userStream.current = stream;

            socketRef.current = io.connect("/");
            socketRef.current.emit("join room", props.match.params.roomID);

            socketRef.current.on('other user', userID => {
                callUser(userID);
                otherUser.current = userID;
            });

            socketRef.current.on("user joined", userID => {
                otherUser.current = userID;
            });

            socketRef.current.on("offer", handleRecieveCall);

            socketRef.current.on("answer", handleAnswer);

            socketRef.current.on("ice-candidate", handleNewICECandidateMsg);
        });

    }, []);

    function callUser(userID) {
        peerRef.current = createPeer(userID);
        userStream.current.getTracks().forEach(track => peerRef.current.addTrack(track, userStream.current));
    }

    function createPeer(userID) {
        const peer = new RTCPeerConnection({
            iceServers: [
                {
                    urls: "stun:stun.stunprotocol.org"
                },
                {
                    urls: 'turn:numb.viagenie.ca',
                    credential: 'batson',
                    username: 'webrtc@live.com'
                },
            ]
        });

        peer.onicecandidate = handleICECandidateEvent;
        peer.ontrack = handleTrackEvent;
        peer.onnegotiationneeded = () => handleNegotiationNeededEvent(userID);

        return peer;
    }

    function handleNegotiationNeededEvent(userID) {
        peerRef.current.createOffer().then(offer => {
            return peerRef.current.setLocalDescription(offer);
        }).then(() => {
            const payload = {
                target: userID,
                caller: socketRef.current.id,
                sdp: peerRef.current.localDescription
            };
            socketRef.current.emit("offer", payload);
        }).catch(e => console.log(e));
    }

    function handleRecieveCall(incoming) {
        peerRef.current = createPeer();
        const desc = new RTCSessionDescription(incoming.sdp);
        peerRef.current.setRemoteDescription(desc).then(() => {
            userStream.current.getTracks().forEach(track => peerRef.current.addTrack(track, userStream.current));
        }).then(() => {
            return peerRef.current.createAnswer();
        }).then(answer => {
            return peerRef.current.setLocalDescription(answer);
        }).then(() => {
            const payload = {
                target: incoming.caller,
                caller: socketRef.current.id,
                sdp: peerRef.current.localDescription
            }
            socketRef.current.emit("answer", payload);
        })
    }

    function handleAnswer(message) {
        const desc = new RTCSessionDescription(message.sdp);
        peerRef.current.setRemoteDescription(desc).catch(e => console.log(e));
    }

    function handleICECandidateEvent(e) {
        if (e.candidate) {
            const payload = {
                target: otherUser.current,
                candidate: e.candidate,
            }
            socketRef.current.emit("ice-candidate", payload);
        }
    }

    function handleNewICECandidateMsg(incoming) {
        const candidate = new RTCIceCandidate(incoming);

        peerRef.current.addIceCandidate(candidate)
            .catch(e => console.log(e));
    }

    function handleTrackEvent(e) {
        partnerVideo.current.srcObject = e.streams[0];
    };

    return (

        <div className="social-box">

                <div className="row1">

               <div className="col-lg-4 col-xs-12 text-center">
                  <div className="box">
                      <i className="fa fa-facebook fa-3x" aria-hidden="true"></i>
                      <div className="box-title">
                          <h3>#3</h3>
                      </div>
                      <div className="box-text">
                          <span> <video width='300px' autoPlay ref={partnerVideo} /></span>
                      </div>
                      <div className="box-btn">

                      </div>
                   </div>
              </div>	 <br/> <br/> <br/><br/>

                <div className="col-lg-15 col-xs-4  text-center">
                   <div className="box">
                       <i className="fa fa-twitter fa-3x" aria-hidden="true"></i>
                       <div className="box-title">
                           <h3>#2</h3>
                       </div>
                       <div className="box-text">
                            {/* <span><video autoPlay ref={partnerVideo} /></span>  */}
                       </div>
                       <div className="box-btn">

                       </div>
                    </div>
               </div>	 
               </div>

               <div className="row2">
               <div className="col-lg-4 col-xs-12 text-center" style={{bacgroundColor:'black'}}>
                   <div className="holder" style={{bacgroundColor:'black'}} >
                       <i className="fa fa-pinterest-p fa-3x" aria-hidden="true"></i>
                       <div className="box-title">
                           <h3>Pinterest</h3>
                       </div>
                       <div className="box-text">
                           <span>  <video width='300px' autoPlay ref={userVideo} />

                             </span>
                       </div>
                       <div className="box-btn">

                       </div>
                    </div>
               </div>	 
               </div>

               <div className="row3">

                <div className="col-lg-4 col-xs-12 text-center">
                   <div className="box">
                       <i className="fa fa-facebook fa-3x" aria-hidden="true"></i>
                       <div className="box-title">
                           <h3>#3</h3>
                       </div>
                       <div className="box-text">
                            {/* <span> <video autoPlay ref={partnerVideo} /></span>  */}
                       </div>
                       <div className="box-btn">

                       </div>
                    </div>
               </div>	 <br/> <br/> <br/><br/>




                <div className="col-lg-4 col-xs-12 text-center">
                   <div className="box">
                       <i className="fa fa-github fa-3x" aria-hidden="true"></i>
                       <div className="box-title">
                           <h3>#4</h3>
                       </div>
                       <div className="box-text">
                             {/* <span><video autoPlay ref={partnerVideo} /></span>   */}
                       </div>
                       <div className="box-btn">

                       </div>
                    </div>
               </div>


   </div>
</div>

    );
};

export default Room;
